package com.example.piaandroidcomposeimport android.os.Bundleimport androidx.activity.ComponentActivityimport androidx.activity.compose.setContentimport androidx.compose.foundation.backgroundimport androidx.compose.foundation.layout.Boximport androidx.compose.foundation.layout.Columnimport androidx.compose.foundation.layout.Rowimport androidx.compose.foundation.layout.Spacerimport androidx.compose.foundation.layout.fillMaxSizeimport androidx.compose.foundation.layout.fillMaxWidthimport androidx.compose.foundation.layout.heightimport androidx.compose.foundation.layout.widthimport androidx.compose.material3.Buttonimport androidx.compose.material3.Textimport androidx.compose.runtime.Composableimport androidx.compose.runtime.getValueimport androidx.compose.runtime.mutableStateOfimport androidx.compose.runtime.rememberimport androidx.compose.runtime.setValueimport androidx.compose.ui.Alignmentimport androidx.compose.ui.Modifierimport androidx.compose.ui.graphics.Colorimport androidx.compose.ui.unit.dpclass Task2 : ComponentActivity() {    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        setContent {            MyApp()        }    }}class List(    item: String)@Composablefun MyApp() {//    val itemsList = remember { mutableListOf<List>() }//    var item by remember { mutableStateOf("") }    var itemList by remember { mutableStateOf(mutableListOf<String>()) }    Column(        modifier = Modifier            .fillMaxSize()            .background(Color.Blue)    ) {        Box(            modifier = Modifier                .fillMaxWidth()                .height(100.dp)                .background(Color.Blue),            contentAlignment = Alignment.Center        ){            Row {               Button(onClick = {                   itemList.add("X")               }) {                   Text("X")               }                Spacer(modifier = Modifier.width(16.dp))                Button(onClick = { itemList.add("Y") }) {                    Text("Y")                }            }        }    }}